import React, { useEffect } from "react";
import { useState } from "react";

import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import Carousel from "react-material-ui-carousel";
import { Paper, Button } from "@material-ui/core";
import AddCircleOutlineIcon from "@material-ui/icons/AddCircleOutline";

import { useSelector, useDispatch } from "react-redux";

import { displayNewJournal } from "../../redux/siteNav/siteNav.actions";
import { selectDispNewJournal } from "../../redux/siteNav/siteNav.selector";

const useStyles = makeStyles((theme) => ({
  root: {
    width: "3em",
    border: "1px solid",
  },
  CheckButton: {
    backgroundColor: theme.palette.secondary.main,
    top: "calc(50% - 20px) !important",
    transition: "40ms",
    cursor: "pointer",
    "&:hover": {
      opacity: "0.6 !important",
    },
    height: "25px",
    width: "100%",
  },
  paperStyle: {
    backgroundColor: "#d6ffb5",
    border: "1px solid",
    marginTop: "0.5em",
  },
  buttonGrid: {
    width: "50%",
  },
  journalTitle: {
    width: "90%",
  },
  journalAddIcon: {
    width: "10%",
  },
  journalIcon: {
    marginRight: "3px",
    marginTop: "3px",
  },
}));

function Item(props) {
  const dispatch = useDispatch();

  //Get navigation state
  const dispNewJournal = useSelector(selectDispNewJournal);
  // console.log("dispNewJournal");
  // console.log(dispNewJournal);

  // Change New Journal option display state
  const togNewJournalDisplay = () => {
    dispatch(displayNewJournal(!dispNewJournal));
  };

  function test() {
    console.log("Test");
  }

  return (
    <Paper className={props.classes.paperStyle}>
      <Grid container direction="column">
        <Grid item container>
          <Grid item className={props.classes.journalTitle}>
            <h2>{props.item.name}</h2>
          </Grid>
          <Grid
            item
            container
            className={props.classes.journalAddIcon}
            justifyContent="flex-end"
          >
            <AddCircleOutlineIcon
              className={props.classes.journalIcon}
              onClick={test}
            />
          </Grid>
        </Grid>
        <Grid item>
          <p>{props.item.description}</p>
        </Grid>
        <Grid item container justifyContent="center">
          <Grid item className={props.classes.buttonGrid}>
            <Button className={props.classes.CheckButton}>Select</Button>
          </Grid>
        </Grid>
      </Grid>
    </Paper>
  );
}

export default function CurrentJournals(props) {
  const classes = useStyles();

  const test = [
    {
      name: "Random Name #1",
      description: "Hello World!",
    },
    {
      name: "Random Name #2",
      description: "Hello, Hello?",
    },
    {
      name: "Random Name #3",
      description: "Hello World!?",
    },
  ];

  const [expanded, setExpanded] = useState(false);
  const [fullJournal, setFullJournal] = useState({
    jName: "",
    jDesc: "",
    roomType: "",
    waterType: "",
    vegLight: "",
    flowLight: "",
    growMedium: "",
  });

  useEffect(() => {}, []);

  return (
    <div>
      <Carousel next={(next, active) => {}} prev={(prev, active) => {}}>
        {test.map((item, i) => (
          <Item key={i} item={item} classes={classes} />
        ))}
      </Carousel>
    </div>
  );
}
