import React, { useEffect } from "react";

//Material UI Components
import { makeStyles } from "@material-ui/core/styles";

import Grid from "@material-ui/core/Grid";
import Paper from "@material-ui/core/Paper";
import ArrowLeftIcon from "@material-ui/icons/ArrowLeft";
import ArrowRightIcon from "@material-ui/icons/ArrowRight";
import CloudIcon from "@material-ui/icons/Cloud";
import Divider from "@material-ui/core/Divider";
import { Typography } from "@material-ui/core";

//Custom useStyles
const useStyles = makeStyles((theme) => ({
  weatSumm: {
    backgroundColor: "white",
    marginTop: "0.5em",
  },
  root: {
    width: "100%",
    maxWidth: 360,
    backgroundColor: theme.palette.secondary.light,
    color: theme.palette.primary.dark,
  },
  summMaxTemp: {
    fontWeight: "bold",
    fontSize: "15pt",
  },
  weekDay: {
    fontWeight: "bold",
    fontSize: "10pt",
  },
  summDate: {
    fontSize: "9pt",
  },
}));

export default function WeatherSummary(props) {
  const classes = useStyles();

  useEffect(() => {}, []);

  //Test data
  const summTemp = [
    {
      maxTemp: "25",
      minTemp: "16",
      icon: "Icon",
      weekDay: "Mon",
      date: "21 | JAN",
    },
    {
      maxTemp: "26",
      minTemp: "17",
      icon: "Icon",
      weekDay: "Tue",
      date: "22 | JAN",
    },
    {
      maxTemp: "28",
      minTemp: "16",
      icon: "Icon",
      weekDay: "Wed",
      date: "23 | JAN",
    },
    {
      maxTemp: "28",
      minTemp: "17",
      icon: "Icon",
      weekDay: "Thu",
      date: "24 | JAN",
    },
  ];

  const newSummTemp = [
    {
      message: "accurate",
      cod: "200",
      count: 5,
      list: [
        {
          id: 2643743,
          name: "London",
          coord: {
            lat: 51.5085,
            lon: -0.1257,
          },
          main: {
            temp: 19.14,
            feels_like: 19.15,
            temp_min: 17.83,
            temp_max: 20.14,
            pressure: 1014,
            humidity: 78,
          },
          dt: 1634641755,
          wind: {
            speed: 1.34,
            deg: 266,
          },
          sys: {
            country: "GB",
          },
          rain: null,
          snow: null,
          clouds: {
            all: 75,
          },
          weather: [
            {
              id: 803,
              main: "Clouds",
              description: "broken clouds",
              icon: "04d",
            },
          ],
        },
        {
          id: 6058560,
          name: "London",
          coord: {
            lat: 42.9834,
            lon: -81.233,
          },
          main: {
            temp: 5.56,
            feels_like: 3.01,
            temp_min: 5.27,
            temp_max: 7.27,
            pressure: 984,
            humidity: 91,
          },
          dt: 1634641746,
          wind: {
            speed: 3.25,
            deg: 271,
          },
          sys: {
            country: "CA",
          },
          rain: null,
          snow: null,
          clouds: {
            all: 0,
          },
          weather: [
            {
              id: 800,
              main: "Clear",
              description: "clear sky",
              icon: "01n",
            },
          ],
        },
        {
          id: 4517009,
          name: "London",
          coord: {
            lat: 39.8865,
            lon: -83.4483,
          },
          main: {
            temp: 6.62,
            feels_like: 6.62,
            temp_min: 3.34,
            temp_max: 9.3,
            pressure: 1023,
            humidity: 87,
          },
          dt: 1634641774,
          wind: {
            speed: 0.45,
            deg: 266,
          },
          sys: {
            country: "US",
          },
          rain: null,
          snow: null,
          clouds: {
            all: 1,
          },
          weather: [
            {
              id: 800,
              main: "Clear",
              description: "clear sky",
              icon: "01n",
            },
          ],
        },
        {
          id: 4298960,
          name: "London",
          coord: {
            lat: 37.129,
            lon: -84.0833,
          },
          main: {
            temp: 5.05,
            feels_like: 5.05,
            temp_min: 3.87,
            temp_max: 6.35,
            pressure: 1024,
            humidity: 93,
          },
          dt: 1634641774,
          wind: {
            speed: 0,
            deg: 0,
          },
          sys: {
            country: "US",
          },
          rain: null,
          snow: null,
          clouds: {
            all: 1,
          },
          weather: [
            {
              id: 800,
              main: "Clear",
              description: "clear sky",
              icon: "01n",
            },
          ],
        },
        {
          id: 5367815,
          name: "London",
          coord: {
            lat: 36.4761,
            lon: -119.4432,
          },
          main: {
            temp: 6.23,
            feels_like: 5.3,
            temp_min: 3.9,
            temp_max: 10.16,
            pressure: 1018,
            humidity: 86,
          },
          dt: 1634641992,
          wind: {
            speed: 1.54,
            deg: 90,
          },
          sys: {
            country: "US",
          },
          rain: null,
          snow: null,
          clouds: {
            all: 1,
          },
          weather: [
            {
              id: 800,
              main: "Clear",
              description: "clear sky",
              icon: "01n",
            },
          ],
        },
      ],
    },
  ];

  console.log(newSummTemp);

  return (
    <Grid
      container
      justifyContent="space-around"
      alignItems="center"
      className={classes.weatSumm}
    >
      <Grid item container alignItems="center">
        <ArrowLeftIcon />
      </Grid>
      {newSummTemp.map((summ, index) => (
        <Grid item style={{ width: "20%" }} key={index}>
          <Paper variant="outlined">
            <Grid container direction="column" className={classes.root}>
              <Grid item container justifyContent="center">
                <Typography className={classes.summMaxTemp}>
                  {summ.maxTemp}
                </Typography>
              </Grid>
              <Grid item container justifyContent="center">
                <Divider style={{ width: "80%", color: "black" }} />
              </Grid>
              <Grid item container justifyContent="center">
                <Typography>{summ.minTemp}</Typography>
              </Grid>
              <Grid item container justifyContent="center">
                <CloudIcon />
              </Grid>
              <Grid item container justifyContent="center">
                <Typography className={classes.weekDay}>
                  {summ.weekDay}
                </Typography>
              </Grid>
              <Grid item container justifyContent="center">
                <Typography className={classes.summDate}>
                  {summ.date}
                </Typography>
              </Grid>
            </Grid>
          </Paper>
        </Grid>
      ))}
      <Grid item container alignItems="center" justifyContent="flex-end">
        <ArrowRightIcon />
      </Grid>
    </Grid>
  );
}
